import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Button, Table } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const Category = () => {


  const [categories,setCategories]=useState([]);

  useEffect(()=>{
    axios.get('http://localhost:5050/catergory').then(res=>{
      setCategories(res.data);
    })
  },[categories])


  return (
    <>
    <Link to='/admin/addCategory' className='btn btn-primary btn-sm mb-4'>Add new Directory</Link>
    <hr/>
    <Table>
      <thead className='mb-4'>
        <th>#</th>
        <th>Name</th>
        <th>Slug</th>
        <th>Action</th>
      </thead>
      <tbody className='mt-3'>
      {
        categories.map((data,index)=>
       <>
        <tr>
        <td>{index}</td>
        <td>{data.CategoryName}</td>
        <td>{data.CategoryEmail}</td>
        <td>
          <Button variant='success' className='btn btn-sm mb-4'>View</Button>
          <Button variant='warning' className='btn btn-sm'>Edit</Button>
          <Button  className='btn btn-sm btn-danger'>Delete</Button>
        </td>
        </tr>
       </>
        )
      }
      </tbody>
    </Table>
    </>
  )
}

export default Category
