import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Button, Form, Table } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const Category = () => {

  // show the api data
  const [categories,setCategories]=useState([]);
  useEffect(()=>{
    axios.get('http://localhost:5050/catergory').then(res=>{
      setCategories(res.data);
    })
  },[categories])

  //delete the single data

  const handleCategoryDelete =(id)=>{
    axios.delete('http://localhost:5050/catergory/' + id)
  }
  //edit single Data

  const [update,setUpdate]=useState(false)

  const handleCategoryEdit=()=>{

    setUpdate(true)

  }


  return (
    <>
    <Link to='/admin/addCategory' className='btn btn-primary btn-sm mb-4'>Add new Directory</Link>
    <hr/>
    <Table>
      <thead className='mb-4'>
        <th>#</th>
        <th>Name</th>
        <th>Slug</th>
        <th>Action</th>
      </thead>
      <tbody className='mt-3'>
      {
        categories.map((data,index)=>
       <>
        <tr>
        <td>{index}</td>
        <td>{data.CategoryName}</td>
        <td>{data.CategoryEmail}</td>
        <td>
          <Button variant='success' className='btn btn-sm mb-4'>View</Button>
          <Button onClick={()=>handleCategoryEdit(data.id)} variant='warning' className='btn btn-sm'>Edit</Button>
          <Button onClick={()=>handleCategoryDelete(data.id)} className='btn btn-sm btn-danger'>Delete</Button>
        </td>
        </tr>
       </>
        )
      }
      </tbody>
    </Table>
    <br /> <br />
      {
        
      update && 
      <Form >
        <Form.Group>
        <Form.Control type='Text' placeholder="Developer's Name"   />
        </Form.Group>
        <Form.Group className='mt-2'>
        <Form.Control type='Text' placeholder="Developer's Email" />
        </Form.Group>
       <Button className="btn-sm" type='submit'>Add New Category</Button>
   </Form>
      }
    </>
  )
}

export default Category
