  const [tag,setTag]=useState('');

    
    const handleDelete= (id)=>{
      console.log(id);
      axios.delete('http://localhost:5050/tags/' + id).then(res=>{
        setTag(res.data)
      })
    }

  useEffect(()=>{
    axios.get('http://localhost:5050/tags').then(res=>{
      setTag(res.data)
    })
  },[tag])



  return (
       <>
